    teamTitle: { en: "Our Leadership", rw: "Abayobozi Bacu", fr: "Notre Direction", sw: "Uongozi Wetu", es: "Nuestra Dirección", de: "Unsere Führung", zh: "我们的领导团队", ar: "قيادتنا", pt: "Nossa Liderança", ja: "私たちの経営陣" },
